import react, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import styled from "styled-components";
import img from "../images/1.png";
import request from "../api"

const Video = ({ video }) => {
  const navigate = useNavigate()
  const [channelIcon, setChannelIcon] = useState(null)

  const {
    id,
    snippet: {
       channelId,
       channelTitle,
       title,
       publishedAt,
       thumbnails: { medium },
    },
    contentDetails,
 } = video
 

 useEffect(() => {
    const get_channel_icon = async () => {
       const {
          data: { items },
       } = await request('/channels', {
          params: {
             part: 'snippet',
             id: channelId,
          },
       })
       setChannelIcon(items[0].snippet.thumbnails.default)
    }
    get_channel_icon()
 }, [channelId])


 const handle = () => {
   navigate(`/details/${id}`)
 }
  return (
    <OneVideo>
      <div className="img">
          <img src={medium.url} onClick={handle} />
      </div>
      <div className="title">
        <span>{title}</span>
      </div>
      <span className="description">
        trying to build the yputbe clone hopefully it works out
      </span>
      <div className="channel">
        <div className="channelimg">
          <img src={channelIcon?.url} />
        </div>
        <span>{channelTitle}</span>
      </div>
    </OneVideo>
  );
};

export default Video;

const OneVideo = styled.div`
  width: 100%;
  min-height: 45vh;
  margin: 10px 10px;
  color: white;
  font-family: Arial;
  .img {
    max-width: 100%;
    height: 20vh;
    overflow: hidden;
    cursor: pointer;
    @media (max-width: 1300px) {
      height: 40vh;
    }
    @media (max-width: 700px) {
      height: 40vh;
    }
    img {
      width: 100%;
    }
  }
  .title {
    font-size: 13px;
    font-weight: 900;
    margin-left: 10px;
  }
  .description {
    margin-left: 10px;
    margin-bottom: 10px;
    font-size: 11px;
  }
  .channel {
    margin-top: 10px;
    display: flex;
    align-items: center;
    justify-content: flex-start;

    .channelimg {
      margin: 0px 10px;
      img {
       width: 35px;
       height: 35px;
      border-radius: 50%;

      }
    }
    span {
      font-size: 14px;
      letter-spacing: 1px;
    }
  }
`;
