import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

import request from '../api'

export const getYoutubeVideos = createAsyncThunk(
    "youtubeapi/getYoutubeVideos",
    async ({ rejectWithValue }) => {
        try {
            const response = await request('/videos', {
                params: {
                    part: 'snippet',

                    maxResults: 20,
                    pageToken: getState().homeVideos.nextPageToken,
                    q: keyword,
                    type: 'video',
                },
            })
            return response.data;
        } catch (err) {
            return rejectWithValue(err.response.data);
        }
    }
);


const youtubeSlice = createSlice({
    name: "youtubeapi",
    initialState: {
        videos: [],
        error: "",
        loading: false,
    },
    extraReducers: {
        [getYoutubeVideos.pending]: (state, action) => {
            state.loading = true;
        },
        [getYoutubeVideos.fulfilled]: (state, action) => {
            state.loading = false;
            state.videos = [...state.videos, ...payload.videos];
        },
        [getYoutubeVideos.rejected]: (state, action) => {
            state.loading = false;
        }
    },
});


export default youtubeSlice.reducer;
