import React from "react";


export default function Form(event) {

    const [formData, setFormData] = React.useState(
        {
        firstName: "",
        password: "",
        confirmP: "",
        checkbox: true
    })
    console.log(formData)
    function handleChange(event) {
        
        const [name, type, value, checkbox] = event.target
        setFormData(prevState => {
            return {
                ...prevState,
         [name]: type === checkbox ? false : value}}
         )
    }

    function btn() {
        event.pre
    
    }
    return (
            <form onSubmit={Form}>
                <input 
                type="text"
                placeholder="First Name"
                onChange={handleChange}
                name = "firstName"
                /> <br />            
                 <input 
                type="password"
                placeholder=" Password"
                onChange={handleChange}
                name = "password"
                /> <br /> 
                 <input 
                type="password"
                placeholder="Confirm Password"
                onChange={handleChange}
                name = "confirmP"
                /> <br /> 
                <input 
                id="checked" 
                type="checkbox" 
                name="checkbox"
                />
                {
                    
                }
                <label htmlFor="checked" >i want to join the news letter</label> <br /> <br />
                <button onClick={btn}>Submit</button>
            </form>
    )
}
