import React from "react";
import memedata from "./memedata";


export default function Form(event) {

    const [formData, setFormData] = React.useState(
        {
        firstName: "",
        password: "",
        confirmP: "",
        joinNewLetter: true
    })
    console.log(formData)
    function handleChange(event) {
        
        const [name, type, value, checked] = event.target
        setFormData(prevState => {
            return {
                ...prevState,
         [name]: type == "checkbox" ? !checked : value}}
         )
    }

    function formSubmit(event) {
        event.preventDefault()

        if (formData.password !== formData.confirmP) {
            console.log("password mismatch")
        } else {
            console.log("success")
        }
    
        if (FormData == false) {
            console.log("you cant joined news letter")
        } else {
            console.log("you joined")
        }
    }

    return (
        <div>
            <form onSubmit={formSubmit}>
                <input 
                type="text"
                placeholder="First Name"
                onChange={handleChange}
                name = "firstName"
                id="first"
                /> <br />            
                 <input 
                type="password"
                placeholder=" Password"
                onChange={handleChange}
                name = "password"
                /> <br /> 
                 <input 
                type="password"
                placeholder="Confirm Password"
                onChange={handleChange}
                name = "confirmP"
                /> <br /> 
                <input 
                type="checkbox"
                id="joinNewLetter"
                onChange={handleChange}
                checked={formData.joinNewLetter}
                />

                <label htmlFor="checked" >i want to join the news letter</label> <br /> <br />
                <button>sign up</button>
            </form>
            <div>
                </div>
                </div>
    )
}
